class Animal:
    def __init__(self, name):
        self.name = name

    def speak(self):
        return "I am an animal"

class Dog(Animal):
    def speak(self):
        return "Woof!"

    def fetch(self):
        return f"{self.name} is fetching the ball"

class Cat(Animal):
    def speak(self):
        return "Meow!"

    def scratch(self):
        return f"{self.name} is scratching"

# Creating objects
dog = Dog("Buddy")
cat = Cat("Whiskers")

print(dog.speak())  # Woof!
print(cat.speak())  # Meow!

print(dog.fetch())  # Buddy is fetching the ball
print(cat.scratch())  # Whiskers is scratching

# Basic try-except
try:
    result = 10 / 0
except ZeroDivisionError:
    print("Cannot divide by zero")

# Multiple exceptions
try:
    number = int("abc")
except ValueError:
    print("Invalid number")
except TypeError:
    print("Type error occurred")

# Try-except-else-finally
try:
    file = open("example.txt", "r")
    content = file.read()
except FileNotFoundError:
    print("File not found")
else:
    print("File read successfully")
    print(content)
finally:
    try:
        file.close()
    except NameError:
        pass  # File was never opened

# Raising exceptions
def validate_age(age):
    if age < 0:
        raise ValueError("Age cannot be negative")
    if age > 150:
        raise ValueError("Age cannot be greater than 150")
    return True

try:
    validate_age(-5)
except ValueError as e:
    print(e)

class CustomError(Exception):
    """Custom exception class"""
    def __init__(self, message, error_code=None):
        super().__init__(message)
        self.error_code = error_code

class ValidationError(CustomError):
    """Exception for validation errors"""
    pass

class DatabaseError(CustomError):
    """Exception for database errors"""
    pass

def validate_user_data(name, age):
    if not name:
        raise ValidationError("Name cannot be empty", "EMPTY_NAME")
    if not isinstance(age, int) or age < 0:
        raise ValidationError("Age must be a positive integer", "INVALID_AGE")
    return True

def save_to_database(data):
    # Simulate database error
    raise DatabaseError("Connection failed", "DB_CONN_ERROR")

# Using custom exceptions
try:
    validate_user_data("", 25)
    save_to_database({"name": "Alice", "age": 25})
except ValidationError as e:
    print(f"Validation error: {e}, Code: {e.error_code}")
except DatabaseError as e:
    print(f"Database error: {e}, Code: {e.error_code}")
except CustomError as e:
    print(f"Custom error: {e}, Code: {e.error_code}")

import unittest

class TestCalculator(unittest.TestCase):

    def setUp(self):
        """Set up test fixtures"""
        self.calc = Calculator()

    def test_addition(self):
        """Test addition functionality"""
        result = self.calc.add(2, 3)
        self.assertEqual(result, 5)

        result = self.calc.add(-1, 1)
        self.assertEqual(result, 0)

    def test_subtraction(self):
        """Test subtraction functionality"""
        result = self.calc.subtract(5, 3)
        self.assertEqual(result, 2)

        result = self.calc.subtract(3, 5)
        self.assertEqual(result, -2)

    def test_division_by_zero(self):
        """Test division by zero raises exception"""
        with self.assertRaises(ZeroDivisionError):
            self.calc.divide(10, 0)

    def tearDown(self):
        """Clean up test fixtures"""
        del self.calc

class Calculator:
    def add(self, a, b):
        return a + b

    def subtract(self, a, b):
        return a - b

    def multiply(self, a, b):
        return a * b

    def divide(self, a, b):
        if b == 0:
            raise ZeroDivisionError("Cannot divide by zero")
        return a / b

if __name__ == '__main__':
    unittest.main()

